import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import ConfigureService from '../Service/ConfigureService'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { CircularProgress, Divider } from "@material-ui/core";
import CollapsableAlert from "../Components/CollapsableAlert/CollapsableAlert";
import DatabaseSelect from "../Components/DatabaseSelect/DatabaseSelect";



class Configure extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        configureService: new ConfigureService(),
        database: null,
        databases: [],
        anchorEl: null,
        loadingCurrentDatabase: true,
        loadingAvailableDatabases: true,
    };


    handleChange = (e) => {
        var database = e.target.value
        console.log(database)

        this.state.configureService.changeDatabases(
            database,
            function (data) {
                console.log("changed db")
                this.setState({ database: database })
            }.bind(this),
            function (data) {
                console.log("did not change db")
            });
    }

    render() {
        return (
            <div>
                <Typography variant="h6" noWrap>Configure Database</Typography>
                <Divider style={{ marginBottom: "2em" }} />
                <DatabaseSelect />

            </div>
        );
    }
}


export default Configure;
