import React, { Component } from "react";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import SavageTable from "../Components/Table/Table";
import TableDataService from "../Service/TableDataServices";
import { Toolbar } from "@material-ui/core";
import ConfigureService from "../Service/ConfigureService";
import CollapsableAlert from "../Components/CollapsableAlert/CollapsableAlert";
import DatabaseSelect from "../Components/DatabaseSelect/DatabaseSelect";

class Tables extends Component {
  state = {
    tables: [],
    tableDataService: new TableDataService(),
    configureService: new ConfigureService(),
    loading: true,
    database: null,
  }

  componentDidMount() {
    this.state.configureService.getCurrentDatabase(
      function (data) {
        this.setState({ database: data.data })
      }.bind(this),
      function (data) {
        this.setState({ database: "N/a" })
        console.log(data)
      }.bind(this));

    this.state.configureService.getTables(
      function (data) {
        this.setState({ tables: data.data })
        this.setState({ loading: false })
      }.bind(this),
      function (data) {
        console.log(data)
        this.setState({ loading: false, connectionError: true })

      }.bind(this));


  }

  render() {
    if (this.state.loading) {
      return (<></>)
    }

    if (this.state.connectionError) {
      return (
        <div>
          <CollapsableAlert severity="error" message="Unable to load table data. Check database connection." />)
          <DatabaseSelect/>
        </div>
      )
    }

    return (
      <ScrollableTabsButtonAuto tables={this.state.tables} database={this.state.database} />
    );
  }
}


function ScrollableTabsButtonAuto(props) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>

      <AppBar position="static" color="default">
        <Toolbar>
          <Typography variant="h6" color="primary">
            {props.database}
          </Typography>
          <Tabs
            selectionFollowsFocus
            value={value}
            onChange={handleChange}
            indicatorColor="primary"
            textColor="primary"
            variant="scrollable"
            scrollButtons="auto"
            aria-label="scrollable auto tabs example"
          >
            {props.tables.map((table, index) => (
              <Tab label={table} {...a11yProps(index)} />
            ))}

          </Tabs>
        </Toolbar>
      </AppBar>

      {props.tables.map((table, index) => (
        <TabPanel value={value} index={index}>
          <SavageTable table={table} />
        </TabPanel>
      ))}
    </div>
  );
}


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    width: '100%',
  },
}));

export default Tables;
