import React, { Component, useEffect, useState } from 'react';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import { green, grey, yellow } from '@material-ui/core/colors';
import StorageIcon from '@material-ui/icons/Storage';
import InfoIcon from '@material-ui/icons/Info';
import AddIcon from '@material-ui/icons/Add';
import MultiSelect from "../MultiSelect/MultiSelect";
import Paper from '@material-ui/core/Paper';
import { Grid } from "@material-ui/core";

const DataGridControlBar = (props) => {
  const { rowCount, columns, onChangeColumnFilter } = props

  return (
    <Grid container direction="row" style={{ backgroundColor: grey[50], borderWidth: 0.5, borderRadius: "10px", borderStyle: "solid", borderColor: grey[300], padding: "0.5em" }}>
      <Grid container >
          <Grid item xs={3} style={{border: "1px solid transparent", borderRadius: "15px", backgroundColor: grey[100], paddingBottom: "0.5em"}}>
            <MultiSelect
              options={columns.map(col => col.column)}
              onChange={onChangeColumnFilter}
              placeholder="Column Filters"
            />
        </Grid>
      </Grid>
    </Grid>

  )
}

export default DataGridControlBar
