import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import ConfigureService from '../Service/ConfigureService'

class Configure extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        configureService: new ConfigureService(),
        database: null,
        databases: []
    };

    componentDidMount() {
        this.loadDatabases()
    }

    loadDatabases = () => {
        this.state.configureService.listAllDatabases(
            function (data) {
                this.setstate({ databases: data.data })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));
    }
    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.database)
        this.state.configureService.changeDatabases(this.state.database,
            function (data) {
                console.log("changed db")
            },
            function (data) {
                console.log("did not change db")
            });
    }

    render() {
        return (
            <>
                <Typography variant="h6" noWrap>Configure Database</Typography>
                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                    Open Menu
                </Button>
                <Menu
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                >
                    {this.state.databases.map(database => (
                        <MenuItem onClick={handleClick}>{database}</MenuItem>
                    ))}

                </Menu>
            </>
        );
    }

    handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    handleClose = () => {
        setAnchorEl(null);
    };

}

export default Configure;
