import React from "react";
import ReactDataGrid, { TextEditor } from "react-data-grid";
import "./styles.css";


class Example extends React.Component {
    state = { 
        rows: this.props.rows,
        tableDataService: new TableDataService(),
        columns: this.createColumns(this.props.columns)
     };

    createColumns(columns) {
        return columns.map((column) => {
            column.editor = TextEditor;
            column.resizable = true;
            return column;
        })}

    onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        this.setState(state => {
            const rows = state.rows.slice();
            for (let i = fromRow; i <= toRow; i++) {
                rows[i] = { ...rows[i], ...updated };
            }
            return { rows };
        });
    };
    
    rowsChanged = (rows, index) => {
        var rowIdx = index.indexes[0];
        var changedRow = this.getChanges(this.state.rows, rows);
        this.state.rows[rowIdx] = changedRow[0];
    
    }

    getChanges = (originalArray, newArray) => newArray.filter((newRow, index) => originalArray[index] !== newRow);

    render() {
        return (
            <ReactDataGrid
                columns={this.state.columns}
                rows={this.state.rows}
                rowGetter={i => this.state.rows[i]}
                onGridRowsUpdated={this.onGridRowsUpdated}
                onRowsChange={this.rowsChanged}
                enableCellSelect={false}
                style={{ resize: 'both', 'height': "70vh", maxWidth: "100%"}}
                className="fill-grid"
                minHeight= "1000"

            />
        );
    }
}

export default Example;
