import React, { Component, useEffect, useState } from 'react';
import DataGrid, { TextEditor } from "react-data-grid";
import TableManager from "./TableManager"
import TableService from '../../Service/TableService';
import { CircularProgress, Typography } from '@material-ui/core';
import CollapsableAlert from '../CollapsableAlert/CollapsableAlert';
import ContentWithContextMenu from '../ContextMenu/ContextMenu';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import { green, grey, yellow } from '@material-ui/core/colors';
import SyncIcon from '@material-ui/icons/Sync';
import StorageIcon from '@material-ui/icons/Storage';
import ContextMenu from '../ContextMenu/ContextMenu';


const contextMenuItems = {
  "Insert": () => { console.log("insert") },
  "Delete": () => { console.log("delete") },
  "Save": () => { console.log("save") },
  "Reload": () => { console.log("reload") }
}

const SavageTable = (props) => {
  const [selectedRow, setSelectedRow] = useState(null)
  const [rows, setRows] = useState([])
  const [columns, setColumns] = useState([])
  const [notification, setNotification] = useState(null)

  const tableService = new TableService()
  const table = props.table

  useEffect(() => {
    tableService.getRowsOfTable(table, function (data) { setRows(data.data.rows) }, function (data) { setNotification(data) })
    tableService.getSchemaOfTable(table, function (data) { setColumns(data.data.columns) }, function (data) { setNotification(data) })
  }, [])

  return (
    <>
      {/* <NotificationArea notification={notification} /> */}
      <ContextMenu menuItems={contextMenuItems} onItemClick={(a) => contextMenuItems[a]()}>
        <SavageDataGrid rows={rows} columns={columns} onRowChange={undefined} onRowSelect={setSelectedRow} />
      </ContextMenu>
      <DataGridControlBar rowCount={rows.length} columnCount={columns.length}/>
    </>
  )
}



const SavageDataGrid = (props) => {
  const columns = props.columns.map(col => ({
    key: col.column,
    name: col.column,
    resizable: true,
    nullable: col.nullable,
    editor: col.editable ? TextEditor : undefined
  }));

  const rows = props.rows
  const onRowChange = props.onRowChange
  const onRowSelect = props.onRowSelect

  return (
    <>
      <DataGrid
        columns={columns}
        rows={rows}
        onRowsChange={onRowChange}
        rowGetter={i => rows[i]}
        enableCellSelect={true}
        style={{ 'height': "65vh", overflowX: 'hidden' }}
        className="fill-grid"
        minHeight="1000"
        onSelectedCellChange={onRowSelect}
      />
    </>
  )
}


const DataGridControlBar = (props) => {
  const rowCount = props.rowCount
  const columnCount = props.columnCount

  return (
    <BottomNavigation showLabels style={{ backgroundColor: grey[50], borderWidth: 0.5, borderStyle: "solid", borderColor: grey[300] }}>
      <BottomNavigationAction label={"Rows: " + rowCount} icon={<StorageIcon />} />
      <BottomNavigationAction label={"Columns: " + columnCount} icon={<StorageIcon />} />
    </BottomNavigation>
  )
}


