import React from 'react';
import { grey } from '@material-ui/core/colors';

import MultiSelect from "../../MultiSelect/MultiSelect";
import { Grid } from "@material-ui/core";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';


const DataGridControlBar = (props) => {
  const { columns, onChangeColumnFilter } = props

  return (
    // <Accordion style={{borderBottom: "1px solid grey", marginBottom: "0px"}}>
    //   <AccordionSummary
    //     style={{ backgroundColor: grey[50], }}
    //     expandIcon={<ExpandMoreIcon />}
    //     aria-controls="panel1a-content"
    //     id="panel1a-header"
    //   >
    //     <Typography variant="button" >Controls</Typography>
    //   </AccordionSummary >
    //   <AccordionDetails style={{ backgroundColor: grey[100], }}>

        <Grid container direction="row" style={{backgroundCOlor: grey[100]}}>
          <Grid container >
            <Grid item xs={2} >
              <MultiSelect
                options={columns.map(col => col.column)}
                onChange={onChangeColumnFilter}
                placeholder="Column Filters"
              />
            </Grid>
          </Grid>
        </Grid>

    //   </AccordionDetails>
    // </Accordion>


  )
}

export default DataGridControlBar
