import React, { Component, useEffect, useState } from 'react';
import DataGrid from "react-data-grid";
import TableService from '../../Service/TableService';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import { green, grey, yellow } from '@material-ui/core/colors';
import StorageIcon from '@material-ui/icons/Storage';
import ContextMenu from '../ContextMenu/ContextMenu';
import DataGridControlBar from './DataGridControlBar';
import DataGridTable from './DataGridTable';




const SavageTable = (props) => {
  const [selectedRow, setSelectedRow] = useState(null)
  const [rows, setRows] = useState([])
  const [columns, setColumns] = useState([])
  const [notification, setNotification] = useState(null)

  const tableService = new TableService()
  const table = props.table
  const contextMenuItems = {
    "Insert": () => { console.log("insert") },
    "Delete": () => { console.log("delete") },
    "Save": () => { console.log("save") },
    "Reload": () => { console.log("reload") }
  }
  useEffect(() => {
    tableService.getRowsOfTable(table, function (data) { setRows(data.data.rows) }, function (data) { setNotification(data) })
    tableService.getSchemaOfTable(table, function (data) { setColumns(data.data.columns) }, function (data) { setNotification(data) })
  
  }, [])


  return (
    <>
      {/* <NotificationArea notification={notification} /> */}
      <ContextMenu
        menuItems={Object.keys(contextMenuItems)}
        onItemClick={(a) => contextMenuItems[a]()}
      >
        <DataGridTable
          rows={rows}
          columns={columns}
          onRowChange={undefined}
          onRowSelect={setSelectedRow}
        />
      </ContextMenu>
      <DataGridControlBar
        rowCount={rows.length}
        columnCount={columns.length}
      />
    </>
  )
}



