import React, { Component, useEffect, useState } from 'react';
import DataGrid from "react-data-grid";
import TableService from '../../Service/TableService';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import { green, grey, yellow } from '@material-ui/core/colors';
import StorageIcon from '@material-ui/icons/Storage';
import ContextMenu from '../ContextMenu/ContextMenu';
import DataGridControlBar from './DataGridControlBar';
import DataGridTable from './DataGridTable';
import CollapsableAlert from '../CollapsableAlert/CollapsableAlert'

const tableService = new TableService()

const contextMenuItems = {
  "Insert": () => { console.log("insert") },
  "Delete": () => { console.log("delete") },
  "Save": () => { console.log("save") },
  "Reload": () => { console.log("reload") }
}


const createNotification = (content, severity) => {
  return {
    content: content,
    severity: severity,
  }
}

const SavageTable = (props) => {
  const [selectedRow, setSelectedRow] = useState(null)
  const [rows, setRows] = useState([])
  const [columns, setColumns] = useState([])
  const [notifications, setNotifications] = useState([])

  const table = props.table

  useEffect(() => {
    tableService.getRowsOfTable(
      table,
      data => setRows(data.data.rows),
      () => setNotifications(notifications => [...notifications, createNotification("Unable to fetch table rows", "error")])
    )

    tableService.getSchemaOfTable(
      table,
      data => setColumns(data.data.columns),
      () => setNotifications(notifications => [...notifications, createNotification("Unable to fetch table schema", "error")])
    )

    setNotifications(notifications => [...notifications, createNotification("Unable to fetch table rows", "error")])
    setNotifications(notifications => [...notifications, createNotification("Unable to fetch table schema", "error")])
  }, [])


  console.log(notifications)

  return (
    <>
      <NotificationArea
        notifications={notifications}
        handleClose={(index) => setNotifications(curr => curr.filter((notifications, i) => i !== index))}
      />

      <ContextMenu
        menuItems={Object.keys(contextMenuItems)}
        onItemClick={(a) => contextMenuItems[a]()}
      >

        <DataGridTable
          rows={rows}
          columns={columns}
          onRowChange={undefined}
          onRowSelect={setSelectedRow}
        />

      </ContextMenu>

      <DataGridControlBar
        rowCount={rows.length}
        columnCount={columns.length}
      />
    </>
  )
}

const NotificationArea = (props) => {
  const notifications = props.notifications
  if (notifications.length == 0) {
    return null
  }
  
  const notificationAlerts = notifications.map(notification =>
    (<CollapsableAlert severity={notification.severity} message={notification.content} onClose={props.handleClose(notifications.indexOf(notification))} />)
  )



  return (
    {notificationAlerts}
  )
}


