import React from "react";
import ReactDataGrid, { TextEditor } from "react-data-grid";
import "./styles.css";
import TableDataService from '../../Service/TableDataServices';
import { CircularProgress } from "@material-ui/core";


class Example extends React.Component {
    componentDidMount() {
        this.state.tableDataService.getTableData(this.props.table,
            function (data) {
                this.setState({ columns: this.createColumns(data.data.tableSchema.columns) })
                this.setState({ rows: this.createRows(data.data.data) })
                this.setState({ loading: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loading: false })
            }.bind(this));

    }

    createColumns(columns) {
        return columns.map(col => {
            var def = {
                key: col.column,
                name: col.column,
                resizable: true
            }
            if (col.editable) {
                def.editor = TextEditor
            }
            return def;
        })
    }

    createRows(data) {

        return data
    }

    insertEmptyRow() {
        var emptyRow = {}
        this.state.columns.forEach(col => {emptyRow[col.key] = ""})
        var data = this.state.data
        data.unshift(emptyRow)
        this.setState({data: data})
    }

    state = {
        rows: [],
        loading: true,
        tableDataService: new TableDataService(),
        columns: [],
        selectedRows: 1,
    };


    onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        this.setState(state => {
            const rows = state.rows.slice();
            for (let i = fromRow; i <= toRow; i++) {
                rows[i] = { ...rows[i], ...updated };
            }
            return { rows };
        });
    };

    rowsChanged = (rows, index) => {
        var rowIdx = index.indexes[0];
        var oldRow = this.state.rows[rowIdx];
        var changedRow = rows[rowIdx];


        if (rowIdx == 0) {
            this.addRow(changedRow, rowIdx)
        } else {
            this.updateRow(oldRow, changedRow, rowIdx)
        }
    }

    addRow = (newRow, index) => {
        var data = { newRow }
        this.state.tableDataService.addTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows.push(newRow)
                this.setState({ rows: rows })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

    updateRow = (oldRow, newRow, index) => {
        var data = [
            {
                oldRow: oldRow,
                newRow: newRow
            },
        ]

        this.state.tableDataService.updateTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows[index] = newRow;
                this.setState({ rows: rows })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

    handleClick(e) {
        if (e.type === 'click') {
            console.log('Left click');
        } else if (e.type === 'contextmenu') {
            e.preventDefault();
            this.insertEmptyRow
            console.log('Right click');
        }
    }

    render() {
        if (this.state.loading) {
            return (<CircularProgress />)
        }

        return (
            <div onClick={this.handleClick} onContextMenu={this.handleClick}>
                <ReactDataGrid
                    columns={this.state.columns}
                    rows={this.state.rows}
                    rowGetter={i => this.state.rows[i]}
                    onGridRowsUpdated={this.onGridRowsUpdated}
                    onRowsChange={this.rowsChanged}
                    enableCellSelect={true}
                    style={{ resize: 'both', 'height': "70vh", maxWidth: "100%" }}
                    className="fill-grid"
                    minHeight="1000"
                />
            </div>
        );
    }
}

export default Example;
