import React from "react";
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 150,
        maxWidth: 300,
    },
    chips: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    chip: {
        margin: 2,
    },
    noLabel: {
        marginTop: theme.spacing(3),
    },
}));


const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
    anchorOrigin: {
        vertical: "top",
        horizontal: "left"
    },
    transformOrigin: {
        vertical: "bottom",
        horizontal: "left"
    },
    getContentAnchorEl: null,
    PaperProps: {
        style: {
            maxHeight: ITEM_HEIGHT * 5 + ITEM_PADDING_TOP,
            width: 250,
        },

    }
};


const MultiSelect = (props) => {
    const classes = useStyles();
    const { placeholder, onChange, options } = props;
    const [selection, setSelection] = React.useState([]);

    const onChangeSelection = (event) => {
        setSelection(event.target.value)
        onChange(event.target.value)
    }

    return (
        <FormControl className={classes.formControl}>
            <InputLabel id="demo-mutiple-checkbox-label">{placeholder}</InputLabel>
            <Select
                labelId="demo-mutiple-checkbox-label"
                id="demo-mutiple-checkbox"
                multiple
                value={selection}
                onChange={onChangeSelection}
                input={<Input />}
                renderValue={(selected) => selected.length > 0 ? selected.length : "No filters"}//selected.join(', ')}
                MenuProps={MenuProps}
            >
                <MenuItem disabled value="">
                    <em>{placeholder}</em>
                </MenuItem>
                {options.map((o) => (
                    <MenuItem key={o} value={o}>
                        <Checkbox checked={selection.indexOf(o) > -1} />
                        <ListItemText primary={o} />
                    </MenuItem>
                ))}
            </Select>
        </FormControl>
    )
}

export default MultiSelect
