import React, { Component, useEffect, useState } from 'react';
import TableServiceHelper from '../../Service/TableServiceHelper';
import ContextMenu from '../ContextMenu/ContextMenu';
import DataGridControlBar from './DataGridControlBar';
import DataGridTable from './DataGridTable';
import NotificationArea from '../NotificationArea/NotificationArea';
import TableService from './TableService';


const SavageTable = (props) => {
  const tableService = new TableService(props.table)
  const [selectedRow, setSelectedRow] = useState(null)
  const [rows, setRows] = useState([])
  const [columns, setColumns] = useState([])
  const [notifications, setNotifications] = useState([])
  const [insertedRows, setInsertedRows] = useState([])

  const addNotification = (content, severity) => {
    const notification = { content: content, severity: severity }
    setNotifications(notifications => [...notifications, notification])
  }

  const loadTableRows = () => {
    tableServiceHelper.getRowSet(data => setRows(data.data.rows), () => addNotification("Unable to fetch table rows", "error"))
  }

  const contextMenuActions = {
    "Insert": () => insertAction,
    "Delete": () => deleteAction,
    "Save": () => saveAction,
    "Reload": loadTableRows
  }

  const insertAction = () => {
    const rId = selectedRow
    setInsertedRows(irs => [irs.map(ir => ir >= rId ? ir + 1 : ir), rId])
    setRows(tableService.addRow(rows, rId))
  }

  const deleteAction = () => {
    const rId = selectedRow
    if(insertedRows.indexOf(rId) != -1) {
      setInsertedRows(irs => irs.filter(r => r === rId))
      return
    }

    tableService.delete(rows, rId, (rows) => {
      setInsertedRows(irs => irs.map(ir => ir > rId ? ir - 1 : ir))
      setRows(rows)
    }, (e) => addNotification("Unable to delete row: " + e, "warning"))
  }

  const saveAction = () => {
    const rId = selectedRow
    
    if(insertedRows.indexOf(rId) == -1) {
      addNotification("The row you are trying to save is already synchronized", "info")
      return
    }

    tableService.save(rows, rId, (rows) => {
      setRows(rows)
      setInsertedRows(irs => irs.filter(r => r === rId))
    }, (e) => addNotification("Unable to save data: " + e, "warning"))
  }

  useEffect(() => {
    loadTableRows()
    tableServiceHelper.getSchema(data => setColumns(data.data.columns), () => addNotification("Unable to fetch table schema", "error"))
  }, [])


  return (
    <>
      <NotificationArea
        notifications={notifications}
        handleClose={(index) => setNotifications(curr => curr.filter((notifications, i) => i !== index))}
      />
      <ContextMenu menuItems={Object.keys(contextMenuActions)} onItemClick={(a) => contextMenuActions[a]()}>
        <DataGridTable
          rows={rows}
          columns={columns}
          onRowSelect={setSelectedRow}
          onRowChange={(newRow, index) => {
            if (insertedRows.indexOf(index) == -1)
              tableService.update(rows, newRow, index, (rows) => setRows(rows), (e) => addNotification("Unable to update row: " + e, "warning"))
          }}
        />
      </ContextMenu>
      <DataGridControlBar rowCount={rows.length} columnCount={columns.length} />
    </>
  )
}


export default SavageTable
