import React, { Component } from 'react';
import { lighten, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Paper from '@material-ui/core/Paper';
import TableDataService from '../../Service/TableDataServices';
import { CircularProgress } from '@material-ui/core';
import SavageDataGrid from './SavageDataGrid';
import Example from './SavageTable';


class SavageTable extends Component {
  createHeaders(row) {
    return Object.keys(row).map(header => (
      {
        field: header,
        headerName: header,
        flex: 1
      }));
  }

  createData(rows) {
    return rows.map((row, index) => {
      row.id = index;
      return row;
    });
  }

  componentDidMount() {
    this.state.tableDataService.getTableData(this.props.table,
      function (data) {
        this.setState({ headers: this.createHeaders(data.data[0]) })
        this.setState({ data: this.createData(data.data) })
        this.setState({ loading: false })
      }.bind(this),
      function (data) {
        console.log(data)
        this.setState({ loading: false })
      }.bind(this));

  }


  state = {
    headers: [],
    data: [],
    tableDataService: new TableDataService(),
    loading: true
  }

  render() {

    if (this.state.loading) {
      return (<CircularProgress />)
    }
    return (
      <Example/>
      // <SavageDataGrid columns={this.state.headers} rows={this.state.data} />
      // <BasicTable headers={this.state.headers} data={this.state.data} />
    )
  }
}

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});


function BasicTable(props) {
  const classes = useStyles();

  return (
    <>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              {props.headers.map(header => (
                <TableCell>{header}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {props.data.map((row) => (
              <TableRow key={row[0]}>
                <TableCell component="th" scope="row">{row[0]}</TableCell>
                {row.slice(1).map((col) => (
                  <TableCell>{col}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}

export default SavageTable;
