import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import ConfigureService from '../Service/ConfigureService'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { CircularProgress, Divider } from "@material-ui/core";
import CollapsableAlert from "../Components/CollapsableAlert/CollapsableAlert";



class Configure extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        configureService: new ConfigureService(),
        database: null,
        databases: [],
        anchorEl: null,
        loadingCurrentDatabase: true,
        loadingAvailableDatabases: true,
    };

    componentDidMount() {
        this.loadDatabases()
    }

    loadDatabases = () => {
        this.state.configureService.getCurrentDatabase(
            function (data) {
                this.setState({ database: data.data })
                this.setState({ loadingCurrentDatabase: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loadingCurrentDatabase: false })
            }.bind(this));

        this.state.configureService.listAllDatabases(
            function (data) {
                this.setState({ databases: data.data })
                this.setState({ loadingAvailableDatabases: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loadingAvailableDatabases: false })
            }.bind(this));

    }

    handleChange = (e) => {
        var database = e.target.value
        console.log(database)

        this.state.configureService.changeDatabases(
            database,
            function (data) {
                console.log("changed db")
                this.setState({ database: database })
            }.bind(this),
            function (data) {
                console.log("did not change db")
            });
    }

    render() {

        if (this.state.loadingCurrentDatabase || this.state.loadingAvailableDatabases) {
            return (
                <>
                    <Typography variant="h6" noWrap>Configure Database</Typography>
                    <CircularProgress />
                </>
                )

        }

        return (
            <>
                {this.state.database == null && <CollapsableAlert severity="warning" message="Unable to fetch current database"/>}
                <Typography variant="h6" noWrap>Configure Database</Typography>
                <Divider style={{ marginBottom: "2em" }} />
                <FormControl style={{ margin: 1, minWidth: 120 }}>
                    <InputLabel shrink={true} id="demo-simple-select-label">Database</InputLabel>
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={this.state.database}
                        onChange={this.handleChange}
                    >
                        {this.state.databases.map(d => (
                            <MenuItem value={d}>{d}</MenuItem>
                        ))}

                    </Select>
                </FormControl>
            </>
        );
    }

}

export default Configure;
