import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import ConfigureService from '../../Service/ConfigureService'

class Configure extends React.Component {

    state = {
        configureService: new ConfigureService(),
        database: null,
    };


    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state.database)
        this.state.configureService.changeDb(this.state.database, function (data) { }, function (data) { });
    }


    useStyles = makeStyles((theme) => ({
        root: {
            '& .MuiTextField-root': {
                margin: theme.spacing(1),
                width: '25ch',
            },
        },
    }));

    render() {
        const classes = this.useStyles();

        return (
            <>
                <Typography variant="h6" noWrap>Configure Database</Typography>
                <form className={classes.root} noValidate autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="db-name" label="Database name" value={this.state.database} onInput={e => this.setState({ database: e.target.value })} />
                    </div>
                </form>

            </>
        );
    }

    DatabaseForm() {
        const classes = this.useStyles();

        return (
            <form className={classes.root} noValidate autoComplete="off" onSubmit={this.handleSubmit}>
                <div>
                    <TextField required id="db-name" label="Database name" value={this.state.database} onInput={e => this.setState({ database: e.target.value })} />
                </div>
            </form>
        );

    }


}

export default Configure;
