import React from "react";
import DataGrid, { TextEditor } from "react-data-grid";
import "./styles.css";
import TableDataService from '../../Service/TableDataServices';
import { CircularProgress } from "@material-ui/core";


class Example extends React.Component {
    state = {
        rows: [],
        columns: [],
        loading: true,
        lastMouseEvent: null,
        tableDataService: new TableDataService(),
    };

    componentDidMount() {
        this.loadTableData()
    }

    loadTableData() {
        this.state.tableDataService.getTableData(this.props.table,
            function (data) {
                this.setState({ columns: this.createColumns(data.data.tableSchema.columns) })
                this.setState({ rows: data.data.data })
                this.setState({ loading: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loading: false })
            }.bind(this));
    }

    createColumns(columns) {
        return columns.map(col => {
            var def = {
                key: col.column,
                name: col.column,
                resizable: true
            }
            if (col.editable) {
                def.editor = TextEditor
            }
            return def;
        })
    }


    render() {
        if (this.state.loading) {
            return (<CircularProgress />)
        }

        return (
            <div>
                <DataGrid
                    columns={this.state.columns}
                    rows={this.state.rows}
                    rowGetter={i => this.state.rows[i]}
                    onRowsChange={this.rowsChanged}
                    enableCellSelect={true}
                    style={{ resize: 'both', 'height': "70vh", maxWidth: "100%" }}
                    className="fill-grid"
                    minHeight="1000"
                    onClick={this.handleClick}
                    onSelectedCellChange={this.handleCellChange}
                    on
                />
            </div>
        );
    }

    handleClick = (e) => {
        this.setState({lastMouseEvent: e})
    }


    handleCellChange = (c) => {

        console.log(this.state.lastMouseEvent)
        if (this.state.lastMouseEvent.type === 'contextmenu') {
            
            console.log("saddsa")
            console.log(c)
            // this.insertEmptyRow(c)
        }
    }

    insertEmptyRow = (r) => {
        console.log(r);
        var emptyRow = {}
        this.state.columns.forEach(col => {emptyRow[col.key] = ""})
        var data = this.state.rows
        data.unshift(emptyRow)
        this.setState({rows: data})
    }


    rowsChanged = (rows, index) => {
        var rowIdx = index.indexes[0];
        var oldRow = this.state.rows[rowIdx];
        var changedRow = rows[rowIdx];


        if (rowIdx == 0) {
            this.addRow(changedRow, rowIdx)
        } else {
            this.updateRow(oldRow, changedRow, rowIdx)
        }
    }

    addRow = (newRow, index) => {
        var data = { newRow }
        this.state.tableDataService.addTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows.push(newRow)
                this.setState({ rows: rows })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

    updateRow = (oldRow, newRow, index) => {
        var data = [
            {
                oldRow: oldRow,
                newRow: newRow
            },
        ]

        this.state.tableDataService.updateTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows[index] = newRow;
                this.setState({ rows: rows })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

}

export default Example;
