import HttpHelper from "../HttpHelper";

class DefinitionService {
    constructor(table) {
        this.table = table
        this.prefix = `/api/definition/${localStorage.getItem('database')}`
        this.httpHelper = new HttpHelper();
    }

    addColumnToTable(table, data, onSuccess, onFailure) {
        this.httpHelper.post(`${this.prefix}/${table}/column/add`, data)
            .then(res => { onSuccess(res) })
            .catch(res => { onFailure(res) });

    }

    dropColumnFromTable(table, column, onSuccess, onFailure) {
        this.httpHelper.post(`${this.prefix}/${table}/${column}/drop`)
            .then(res => { onSuccess(res) })
            .catch(res => { onFailure(res) });

    }

}

export default DefinitionService;
