import React, { Component } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { lighten, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Paper from '@material-ui/core/Paper';


class SavageTable extends Component {
  componentDidMount() {
    this.state.tableDataService.getTableData(props.table, function (data) {
      this.setState({ headers: createHeaders(data.data[0]) })
      this.setState({ data: createData(data.data) })
      this.setState({ loading: false })
    }.bind(this), function (data) {
      console.log(data)
    });
  }

  createHeaders(row) {
    return Object.keys(row);
  }

  createHeaders(rows) {
    return rows.map(row => Object.values(row));
  }

  state = {
    headers: [],
    data: [],
    tableDataService: new TableDataService(),
    loading: true
  }

  render() {
    return (
      <BasicTable headers={this.state.headers} data={this.state.data}/>
    )
  }
}

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});


const rows = [
  ['Frozen yoghurt', 159, 6.0, 24, 4.0],
  ['Ice cream sandwich', 237, 9.0, 37, 4.3],
  ['Eclair', 262, 16.0, 24, 6.0],
  ['Cupcake', 305, 3.7, 67, 4.3],
  ['Gingerbread', 356, 16.0, 49, 3.9],
];

function BasicTable(props) {
  const classes = useStyles();

  return (
    <>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              {props.headers.map(header => (
                <TableCell>{header}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {props.data.map((row) => (
              <TableRow key={row[0]}>
                <TableCell>{row[0]}</TableCell>
                {row.slice(1).map((col) => (
                  <TableCell>{col}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}

export default SavageTable;
