import React, { Component, useEffect, useState } from 'react';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import { green, grey, yellow } from '@material-ui/core/colors';
import StorageIcon from '@material-ui/icons/Storage';
import InfoIcon from '@material-ui/icons/Info';
import { makeStyles } from '@material-ui/core/styles';

import AddIcon from '@material-ui/icons/Add';
import MultiSelect from "../MultiSelect/MultiSelect";
import Paper from '@material-ui/core/Paper';
import { Grid } from "@material-ui/core";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

const DataGridControlBar = (props) => {
  const classes = useStyles();

  const { rowCount, columns, onChangeColumnFilter } = props

  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-controls="panel1a-content"
        id="panel1a-header"
      >
        <Typography variant="h5" className={classes.heading}>Table Controls</Typography>
      </AccordionSummary>
      <AccordionDetails>
        <Grid container direction="row" style={{ backgroundColor: grey[50], borderWidth: 0.5, borderRadius: "10px", borderStyle: "solid", borderColor: grey[300], padding: "0.5em" }}>
          <Grid container >
            <Grid item xs={3} style={{ border: "1px solid transparent", borderRadius: "15px", backgroundColor: grey[100], paddingBottom: "0.5em" }}>
              <MultiSelect
                options={columns.map(col => col.column)}
                onChange={onChangeColumnFilter}
                placeholder="Column Filters"
              />
            </Grid>
          </Grid>
        </Grid>
      </AccordionDetails>
    </Accordion>


  )
}

export default DataGridControlBar
