import React, { Component, useState } from 'react';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import './react-contextmenu.css'

const ContextMenu = (props, { children }) => {
    const [mouseY, setMouseY] = useState(null)
    const [mouseX, setMouseX] = useState(null)
    const menuItems = props.menuItems.map((text) =>
        <MenuItem onClick={() => props.onItemClick(text)}>{text}</MenuItem>
    )
    const handleClose = () => {
        setMouseX(null)
        setMouseY(null)
    }
    const handleClick = (e) => {
        e.preventDefault()
        setMouseX(e.clientX - 2)
        setMouseY(e.clientY - 4)
    }

    return (
        <div onClick={handleClose} onContextMenu={handleClick} style={{ cursor: 'context-menu' }}>
            {children}
            <Menu
                keepMounted
                open={mouseY !== null}
                onClose={handleClose}
                anchorReference="anchorPosition"
                anchorPosition={
                    mouseY !== null && mouseX !== null
                        ? { top: mouseY, left: mouseX }
                        : undefined
                }
            >
                {menuItems}
            </Menu>
        </div>
    )

}


export default ContextMenu;
