import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

import Component from "mson-react/lib/component";

const definition = {
    component: "Form",
    fields: [
        {
            name: "heading",
            component: "Text",
            text: "Test"
        },
        {
            name: "Column Name",
            component: "Text",
            required: true
        },
        {
            name: "Primary Key",
            component: "BooleanField",
        },
        {
            name: "submit",
            component: "ButtonField",
            label: "Submit",
            type: "submit",
            icon: "Send"
        }
    ]
};

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
}));


export default function CreateTable() {
    return (
        <>
            <Component
                definition={definition}
                onSubmit={({ component }) => {
                    alert(JSON.stringify(component.getValues()));
                }}
            />,
            <Typography variant="h6" noWrap>Create new table</Typography>
            <CreateTableForm />

        </>
    );
}


function CreateTableForm() {

    const classes = useStyles();

    return (
        <form className={classes.root} noValidate autoComplete="off">
            <div>
                <TextField required id="table-name" label="Table name" />
            </div>
            <CreateColumnEntry />
        </form>
    );

}

function CreateColumnEntry() {
    return (
        <div>
            <TextField id="column-name" label="Column Name" />
            <FormControlLabel
                control={
                    <Checkbox
                        // checked={state.checkedB}
                        // onChange={handleChange}
                        name="checkedB"
                        color="primary"
                    />
                }
                labelPlacement="top"

                label="Primary Key"
            />
        </div>
    )
}
