import React, { Component, useEffect, useState } from 'react';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import { green, grey, yellow } from '@material-ui/core/colors';
import StorageIcon from '@material-ui/icons/Storage';
import InfoIcon from '@material-ui/icons/Info';
import AddIcon from '@material-ui/icons/Add';
import MultiSelect from "../MultiSelect/MultiSelect";
import Paper from '@material-ui/core/Paper';
import { Grid, Typography } from "@material-ui/core";

const DataGridControlBar = (props) => {
  const { rowCount, columns } = props
  return (
    <Grid container style={{ backgroundColor: grey[50], borderWidth: 0.5, borderStyle: "solid", borderColor: grey[300], padding: "1em" }}>

      <Grid container direction="row" alignItems="center" spacing={2}>
        <Paper>
          <Grid item>
            <Typography variant="button">Column filters</Typography>
          </Grid>
          <Grid item xs={3}>
            <MultiSelect
              options={columns.map(col => ({ value: col.column, label: col.column }))}
            // onChange={(fs) => setColumnFilter(fs.map(f => f.value))}
            />
          </Grid>
        </Paper>
      </Grid>
    </Grid >

  )
}

export default DataGridControlBar
