import React from "react";
import ReactDataGrid, { TextEditor } from "react-data-grid";
import "./styles.css";
import TableDataService from '../../Service/TableDataServices';
import { CircularProgress } from "@material-ui/core";


class Example extends React.Component {
    componentDidMount() {
        this.state.tableDataService.getTableData(this.props.table,
            function (data) {
                this.setState({ columns: this.createColumns(data.data[0]) })
                this.setState({ rows: this.createRows(data.data) })
                this.setState({ loading: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loading: false })
            }.bind(this));

    }

    createColumns(row) {
        return Object.keys(row).map(header => (
            {
                key: header,
                name: header,
                editor: TextEditor,
                resizable: true
            }));
    }

    createRows(data) {
        var emptyRow = Object.keys(data[0]).map(key => ({key: ""}))
        data.unshift(emptyRow)
        return data
    }

    state = {
        rows: [],
        loading: true,
        tableDataService: new TableDataService(),
        columns: []
    };


    onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        this.setState(state => {
            const rows = state.rows.slice();
            for (let i = fromRow; i <= toRow; i++) {
                rows[i] = { ...rows[i], ...updated };
            }
            return { rows };
        });
    };

    rowsChanged = (rows, index) => {
        var rowIdx = index.indexes[0];
        var oldRow = this.state.rows[rowIdx];
        var changedRow = rows[rowIdx];


        console.log(data);

        if(rowIdx == 0) {
            console.log("NEW ROW!!")
        } else {
            this.updateRow(oldRow, changedRow, rowIdx)
        }
    }

    updateRow = (oldRow, newRow, index) => {
        var data = [
            {
                oldRow: oldRow,
                newRow: newRow
            },
        ]

        this.state.tableDataService.updateTableData(this.props.table, data,
            function(data) {
                var rows = this.state.rows;
                rows[index] = newRow;
                this.setState({rows: rows})
            }.bind(this),
            function(data) {
                console.log(data)
            }.bind(this));

    }

    render() {
        if (this.state.loading) {
            return (<CircularProgress />)
        }

        return (
            <ReactDataGrid
                columns={this.state.columns}
                rows={this.state.rows}
                rowGetter={i => this.state.rows[i]}
                onGridRowsUpdated={this.onGridRowsUpdated}
                onRowsChange={this.rowsChanged}
                enableCellSelect={false}
                style={{ resize: 'both', 'height': "70vh", maxWidth: "100%" }}
                className="fill-grid"
                minHeight="1000"

            />
        );
    }
}

export default Example;
