import React, { Component } from "react";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import SavageTable from "./Table";
import TableDataService from "../../Service/TableDataServices";
import CircularProgress from '@material-ui/core/CircularProgress';

class Tables extends Component {
  state = {
    tables: [],
    tableDataService: new TableDataService(),
    loading: true
  }

  componentDidMount() {
    this.state.tableDataService.getTables(
      function (data) {
        this.setState({ tables: data.data })
        this.setState({ loading: false })
      }.bind(this),
      function (data) {
        console.log(data)
      });
  }



  render() {
    if (this.state.loading) {
      return (<CircularProgress />)
    }

    return (
      <ScrollableTabsButtonAuto tables={this.state.tables} />
    );
  }
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-auto-tabpanel-${index}`}
      aria-labelledby={`scrollable-auto-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-auto-tab-${index}`,
    'aria-controls': `scrollable-auto-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    width: '100%',
    height: "80%",
    backgroundColor: theme.palette.background.paper,
  },
}));

function ScrollableTabsButtonAuto(props) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          selectionFollowsFocus
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
          aria-label="scrollable auto tabs example"
        >
          {props.tables.map((table, index) => (
            <Tab label={table} {...a11yProps(index)} />
          ))}

        </Tabs>
      </AppBar>

      {props.tables.map((table, index) => (
        <TabPanel value={value} index={index}>
          <SavageTable table={table} />
        </TabPanel>
      ))}
    </div>
  );
}

export default Tables;
