import DataGrid, { TextEditor } from "react-data-grid";
import { createPortal } from 'react-dom';
import { ContextMenu, MenuItem, SubMenu, ContextMenuTrigger } from 'react-contextmenu';

import './highlight.css'


const DataGridTable = (props) => {
    if (props.columns.length == 0) {
        return null
    }
    const columns = props.columns.map(col => ({
        key: col.column,
        name: col.column,
        resizable: true,
        nullable: col.nullable,
        editor: col.editable ? TextEditor : undefined
    }));

    var rows = [...props.rows]
    const onRowChange = props.onRowChange
    const onRowSelect = props.onRowSelect

    const onRowDelete = (e, { rowIdx }) => {
        console.log(e)
        console.log(rowIdx)
    }

    const onRowInsertAbove = (e, { rowIdx }) => {
        console.log(e)
        console.log(rowIdx)
    }

    const onRowInsertBelow = (e, { rowIdx }) => {
        console.log(e)
        console.log(rowIdx)
    }

    return (
        <>
            <DataGrid
                columns={columns}
                rows={rows}
                onRowsChange={(updatedRows, index) => {
                    var idx = index.indexes[0]
                    rows[idx] = updatedRows[idx]
                    onRowChange(updatedRows[idx], idx)
                }}
                rowGetter={i => rows[i]}
                enableCellSelect={true}
                style={{ 'height': "65vh", overflowX: 'hidden' }}
                className="fill-grid"
                rowClass={row => props.highlightRows.includes(rows.indexOf(row)) ? "row-highlighted" : "row"}
                minHeight="1000"
                onSelectedCellChange={(o) => onRowSelect(o.rowIdx)}
            />
            {createPortal(
                <ContextMenu id="grid-context-menu">
                    <MenuItem onClick={onRowDelete}>Delete Row</MenuItem>
                    <SubMenu title="Insert Row">
                        <MenuItem onClick={onRowInsertAbove}>Above</MenuItem>
                        <MenuItem onClick={onRowInsertBelow}>Below</MenuItem>
                    </SubMenu>
                </ContextMenu>,
                document.body
            )}
        </>
    )
}

export default DataGridTable
