import React from "react";
import DataGrid, { TextEditor } from "react-data-grid";
import "./styles.css";
import TableDataService from '../../Service/TableDataServices';
import { CircularProgress } from "@material-ui/core";


class Example extends React.Component {
    
    state = {
        rows: [],
        rowStagedForChange: -1,
        newRows: [],
        columns: [],
        loading: true,
        selectedRow: 0,
        tableDataService: new TableDataService(),
    };

    componentDidMount() {
        this.loadTableData()
    }

    loadTableData() {
        this.state.tableDataService.getTableData(this.props.table,
            function (data) {
                this.setState({ columns: this.createColumns(data.data.tableSchema.columns) })
                this.setState({ rows: data.data.data })
                this.setState({ loading: false })
            }.bind(this),
            function (data) {
                console.log(data)
                this.setState({ loading: false })
            }.bind(this));
    }

    createColumns(columns) {
        return columns.map(col => {
            var def = {
                key: col.column,
                name: col.column,
                resizable: true
            }
            if (col.editable) {
                def.editor = TextEditor
            }
            return def;
        })
    }


    render() {
        if (this.state.loading) {
            return (<CircularProgress />)
        }

        return (
            <div onContextMenu={this.handleClick}>
                <DataGrid
                    columns={this.state.columns}
                    rows={this.state.rows}
                    rowGetter={i => this.state.rows[i]}
                    onRowsChange={this.rowsChanged}
                    enableCellSelect={true}
                    style={{ resize: 'both', 'height': "70vh", maxWidth: "100%" }}
                    className="fill-grid"
                    minHeight="1000"
                    onSelectedCellChange={this.handleCellChange}
                    on
                />
            </div>
        );
    }

    handleClick = (e) => {
        if (e.type === 'contextmenu') {
            e.preventDefault()
            this.insertEmptyRow(this.state.selectedRow)
        }
    }


    handleCellChange = (c) => {
        if(c.rowIdx != this.state.selectedRow && this.state.rowStagedForChange != -1) { // Changed row selection and a row is staged for change
            var newRowIdx = this.state.newRows.indexOf(indexOfChangedRow) 
            if (newRowIdx = -1) { // See if index of changed row exists in list of new rows
                this.addRow(newInstanceOfChangedRow, newRowIdx)
            } else {
                this.updateRow(oldInstanceOfChangedRow, newInstanceOfChangedRow, indexOfChangedRow)
            }
        }

        this.state.selectedRow = c.rowIdx
    }

    insertEmptyRow = (idx) => {
        var emptyRow = {}
        this.state.columns.forEach(col => {emptyRow[col.key] = ""})
        var data = this.state.rows
        data.splice(idx, 0, emptyRow) //Insert empty row at index idx
        this.setState({rows: data})
        this.state.newRows.push(idx)
    }


    rowsChanged = (rows, index) => {
        var indexOfChangedRow = index.indexes[0]; 
        if(indexOfChangedRow == this.state.selectedRow) {
            console.log("new shit but row still selected")
            return
        }
        console.log("new shit with row unselected")
        var oldInstanceOfChangedRow = this.state.rows[indexOfChangedRow];
        var newInstanceOfChangedRow = rows[indexOfChangedRow];
        
        this.setState({rowStagedForChange: indexOfChangedRow})
        var newRowIdx = this.state.newRows.indexOf(indexOfChangedRow) 
        if (newRowIdx = -1) { // See if index of changed row exists in list of new rows
            this.addRow(newInstanceOfChangedRow, newRowIdx)
        } else {
            this.updateRow(oldInstanceOfChangedRow, newInstanceOfChangedRow, indexOfChangedRow)
        }
    }

    addRow = (newInstanceOfChangedRow, newRowIdx) => {
        var data = { newRow: newInstanceOfChangedRow}
        console.log(data)
        this.state.tableDataService.addTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows.push(newRowIdx)
                this.setState({ rows: rows })
                this.state.newRows.splice(newRowIdx, 1); // Unmark row as new
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

    updateRow = (oldRow, newRow, index) => {
        var data = [
            {
                oldRow: oldRow,
                newRow: newRow
            },
        ]

        this.state.tableDataService.updateTableData(this.props.table, data,
            function (data) {
                var rows = this.state.rows;
                rows[index] = newRow;
                this.setState({ rows: rows })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

}

export default Example;
