import TableService from '../../Service/TableService';


class TableManager {
    constructor(table) {
        this.table = table
        this.tableService = new TableService()
        this.unsynchronizedRows = []
        this.rows = []
        console.debug("Initialized table manager for table " + table)
    }

    loadTableRowSetFromDatabase() {
        var rows = []
        this.tableService.getRowsOfTable(
            this.table,
            function (data) {
                rows = data.data.rows
                console.debug("Fetched rowset from database for table " + this.table + ". Number of rows: " + rows.length)
            }.bind(this),
            function (data) {
                console.debug("An error occured during fetching rowset of table " + this.table + " from database. ( " + data.data + ")")
            }.bind(this),
        )

        return rows
    }

    getRowSet() {
        if (this.rows.length == 0) {
            this.loadTableRowSetFromDatabase();
        }
        return this.rows
    }

    getRow(index) {
        return this.getRowSet()[index]
    }

    addRow(columns, index) {
        var copyOfRows = [...this.rows]
        var newRow = {}
        columns.forEach(col => { newRow[col] = "" })
        copyOfRows.splice(index, 0, newRow)
        this.rows = copyOfRows;
        this.updateUnsynchronizedRowsAfterInsertion(index);
        console.debug("Inserted a new row at index " + index)
    }


    update(updatedRow, index) {
        var oldRow = this.rows[index]
        var newRowSet = [...this.rows]
        var rowUpdateData = { oldRow: oldRow, newRow: updatedRow }

        this.tableService.addRowToTable(
            this.table,
            rowUpdateData,
            function () {
                newRowSet[index] = updatedRow;
                console.debug("Added row to database for table " + this.table)
            }.bind(this),
            function (data) {
                console.debug("Error occured during adding row to database for table " + this.table + "(" + data.data + ")")
            }.bind(this)
        );

        this.rows = newRowSet
    }

    delete(index) {
        if (this.unsynchronizedRows.includes(index)) {
            this.deleteRowFromLocalTable(index)
        } else {
            var data = { row: this.rows[index] }
            this.tableService.deleteRowOfTable(
                this.table,
                data,
                function () {
                    this.deleteRowFromLocalTable(index)
                }.bind(this),
                function (data) {
                    console.log(data)
                }
            )
        }
    }

    addUnsynchronizedRowsToDatabase() {
        var exceptions = []
        var addedRows = []

        this.unsynchronizedRows.forEach(index => {
            this.tableService.addRowToTable(this.table, { row: this.rows[index] },
                function (data) {
                    var synchronizedRow = data.data.row
                    this.rows[index] = synchronizedRow
                    addedRows.push(index)
                }.bind(this),
                function (data) { exceptions.push({ index: index, message: data.data }) }
            )
        })

        addedRows.forEach(i => this.deleteRowFromUnsynchronizedRowsIfPresent(i))

        return exceptions
    }

    deleteRowFromLocalTable(index) {
        var newRowSet = [...this.rows]
        newRowSet.splice(index, 1)
        this.rows = newRowSet;
        this.updateUnsynchronizedRowsAfterDeletion(index);
    }

    updateUnsynchronizedRowsAfterInsertion(index) {
        this.unsynchronizedRows = this.unsynchronizedRows.map(i => (index <= i ? i + 1 : i))
        this.this.unsynchronizedRows.push(index)
    }

    updateUnsynchronizedRowsAfterDeletion(index) {
        this.deleteRowFromUnsynchronizedRowsIfPresent(index)
        this.unsynchronizedRows = this.unsynchronizedRows.map(i => (index <= i ? i - 1 : i))
    }

    deleteRowFromUnsynchronizedRowsIfPresent(index) {
        var indexInList = this.unsynchronizedRows.indexOf(index)
        if (indexInList > -1) this.unsynchronizedRows.splice(indexInList, 1)
    }
}
export default TableManager
