import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';



const [database, setDatabase] = useState('');
const configureService = new ConfigureService();

function handleSubmit(event) {
    event.preventDefault();
    console.log(database)
    configureService.changeDb(database,  function (data) {},  function (data) {});
}


const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
}));


export default function CreateTable() {
    return (
        <>
            <Typography variant="h6" noWrap>Configure Database</Typography>
            <DatabaseForm />

        </>
    );
}


function DatabaseForm() {

    const classes = useStyles();

    return (
        <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit}>
            <div>
                <TextField required id="db-name" label="Database name" value={database} onInput={ e=>setDatabase(e.target.value)} />
            </div>
        </form>
    );

}
