import React from "react";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import ConfigureService from '../Service/ConfigureService'
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
class Configure extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        configureService: new ConfigureService(),
        database: null,
        databases: [],
        anchorEl: null
    };

    componentDidMount() {
        this.loadDatabases()
    }

    loadDatabases = () => {
        this.state.configureService.listAllDatabases(
            function (data) {
                this.setState({ databases: data.data })
            }.bind(this),
            function (data) {
                console.log(data)
            }.bind(this));

    }

    handleChange = (database) => {
        console.log(database)
        this.state.configureService.changeDatabases(database,
            function (data) {
                console.log("changed db")
            },
            function (data) {
                console.log("did not change db")
            });
    }

    render() {

        return (
            <>
                <Typography variant="h6" noWrap>Configure Database</Typography>
                <FormControl className={classes.formControl}>
                    <InputLabel id="demo-simple-select-label">Database</InputLabel>
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        value={database}
                        onChange={handleChange}
                    >
                        {this.state.databases.map(d => (
                            <MenuItem value={d}>{d}</MenuItem>    
                        ))}
                    
                    </Select>
                </FormControl>
            </>
        );
    }

}

export default Configure;
